$(function() {
	$("*[include-html]").each(function() {
		var i = $(this).attr("include-html"),
			s = this.id;
		jQuery.ajax({
			url: i,
			success: function(t) {
				$("#" + s).html(t)
			},
			error: function(t, u, n) {
				var c = t.status + ": " + t.statusText;
				$("#" + s).html(i + "-" + c)
			}
		})
	})
});